using System;
using System.Configuration;
using MongoDB.Driver;
using MongoDB.Bson;
using System.Diagnostics;
using System.Threading;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace cosmos_db_mongodb_multithread
{
    class cosmos_mongo
    {
        private MongoUrl mongoUrl;
        private MongoClientSettings mongoClientSettings;
        private MongoClient mongoClient;
        private IMongoDatabase mongoDatabase;
        private IMongoCollection<BsonDocument> mongoCollection;
        public cosmos_mongo()
        {
            string mongoConnectionStringRaw = ConfigurationManager.AppSettings["connectionstring"];

            if (string.IsNullOrWhiteSpace(mongoConnectionStringRaw))
            {
                Console.WriteLine(DateTime.UtcNow.ToString("yyyy-MM-dd HH:mm:ss.fff") + ", ERROR:connectionstring is empty.");
                return;
            }
            try
            {
                mongoUrl = new MongoUrl(mongoConnectionStringRaw);
                mongoClientSettings = MongoClientSettings.FromUrl(mongoUrl);
                mongoClientSettings.ReadPreference = ReadPreference.Nearest;
                mongoClient = new MongoClient(mongoClientSettings);
                mongoDatabase = mongoClient.GetDatabase(ConfigurationManager.AppSettings["database"]);
                mongoCollection = mongoDatabase.GetCollection<BsonDocument>(ConfigurationManager.AppSettings["collection"]);
            }
            catch (Exception e)
            {
                Console.WriteLine(e.Message);
                Console.WriteLine(DateTime.UtcNow.ToString("yyyy-MM-dd HH:mm:ss.fff") + ", ERROR:connectionstring is invalid.");
            }
        }

        [Obsolete]
        public void Insert(int _times)
        {
            Stopwatch stopWatch = new Stopwatch();
            BsonDocument template = new BsonDocument(
                new BsonElement("_Id", new BsonString(Guid.NewGuid().ToString())),
                new BsonElement("writtenTime", new BsonDateTime(DateTime.UtcNow)),
                #region document payload
                new BsonElement("writtenText01", new BsonString("")),
                new BsonElement("writtenText02", new BsonString("")),
                new BsonElement("writtenText03", new BsonString("")),
                new BsonElement("writtenText04", new BsonString("")),
                new BsonElement("writtenText05", new BsonString("")),
                new BsonElement("writtenText06", new BsonString("")),
                new BsonElement("writtenText07", new BsonString("")),
                new BsonElement("writtenText08", new BsonString("")),
                new BsonElement("writtenText09", new BsonString("")),
                new BsonElement("writtenText10", new BsonString("")),
                new BsonElement("writtenText11", new BsonString("")),
                new BsonElement("writtenText12", new BsonString("")),
                new BsonElement("writtenText13", new BsonString("")),
                new BsonElement("writtenText14", new BsonString("")),
                new BsonElement("writtenText15", new BsonString("")),
                new BsonElement("writtenText16", new BsonString("")),
                new BsonElement("writtenText17", new BsonString("")),
                new BsonElement("writtenText18", new BsonString("")),
                new BsonElement("writtenText19", new BsonString("")),
                new BsonElement("writtenText20", new BsonString(""))
                #endregion
            );

            try
            {
                stopWatch.Start();
                for (int i = 0; i < _times; ++i)
                {
                    BsonDocument toInsert = (BsonDocument)template.DeepClone();
                    mongoCollection.InsertOne(toInsert);
                    //mongoCollection.InsertOneAsync(toInsert);
                    //Thread.Sleep(1000);
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex.Message);
            }
            Console.WriteLine(DateTime.UtcNow.ToString("yyyy-MM-dd HH:mm:ss.fff") + $", thread completed in {stopWatch.ElapsedMilliseconds} milliseconds.");
        }

    }
}
